const choco = {};
const fs = require( 'fs' )
module.exports = {
    establecerD1: function ( directorio, valor, valor2 ) {
        if ( !directorio ) return console.log( 'El directorio no fue especificado' )
        if ( !valor ) return console.log( 'El primer valor no fue especificado' )
        if ( !valor2 ) return console.log( 'El segundo valor no fue especificado' )
        fs.writeFile( `./choco_folder/${ directorio }.json`, `{\n"${ valor }":"${ valor2 }"\n}`, ( err ) => {
            if ( err ) {
                console.log( 'No existe la carpeta y/o archivo para establecer, revisa y intentalo nuevamente' )
            }
        } )
    }
}




