const fs = require( 'fs' )
const choco = {}
module.exports = {
    restablecer: function ( directorio ) {
        if ( !directorio ) return console.log( 'El directorio no fue especificado' )
        fs.writeFile( `./choco_folder/${ directorio }.json`, '{}', ( err ) => {
            if ( err ) {
                console.log( 'No existe la carpeta y/o archivo para resetear, revisa y intentalo nuevamente' )
            }
        } )
    }
}   